import React from 'react'
import App, { Container } from 'next/app'
import * as SentryNode from '@sentry/browser';
// import * as SentryNode from '@sentry/node';
const axios = require('axios');

SentryNode.init({
    dsn: "https://f9b7080967954f65a3cfc353b66f708d@sentry.io/1384259",
    autoBreadcrumbs: true,
    captureUnhandledRejections: true,
});

class MyApp extends App {
    constructor(...args) {
        super(...args);


        //
        // Sentry.init({
        //     dsn: "https://f9b7080967954f65a3cfc353b66f708d@sentry.io/1384259",
        //     autoBreadcrumbs: true,
        //     captureUnhandledRejections: true,
        // });
    }

    static async getInitialProps({ Component, router, ctx }) {
        try {
            throw new Error('wow here is an error')
        } catch(e) {
            SentryNode.captureException(e);
            console.log(e)
        }
        SentryNode.captureException('throwing another exception')

        let pageProps = {};


        try {

            if (Component.getInitialProps) {
                pageProps = await Component.getInitialProps(ctx)
                if (ctx.err) {
                    console.log('working');
                    axios.post('http://localhost:5000', { err: JSON.stringify(ctx.err.toString() + ctx.err.stack.toString())})
                    SentryNode.captureException(ctx.err.toString() + 'new')

                    console.log('AFTERRRRRR');
                }
                }
            }
         catch (error) {
            console.log('whoaaaa there was an error');
            // console.log(error);
        }

        return { pageProps }
    }

    render() {
        // SentryNode.captureException("hi thanks for the error")

        const { Component, pageProps } = this.props;

        return (
            <Container><div>this is a test</div>
                <Component {...pageProps} />
            </Container>
        )
    }
}

export default MyApp;
